import "./App.css";
import Common from "./components/admin/Common";
import React from 'react';
import ReactDOM from "react-dom";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Header from './components/admin/layout/Header.js';
import Sidebar from './components/admin/layout/Sidebar.js';
import RecentOrders from './components/admin/RecentOrders.js';
//import "./Common.css";
import OrderDetails from './components/admin/OrderDetails.js';
import MenuList from './components/admin/MenuList.js';
import AddToMenu from './components/admin/AddToMenu.js';
import EditMenu from './components/admin/EditMenu.js';
import MyCart from '../components/admin/MyCart'

function App(){
    return(
        <BrowserRouter>
            <Header/>
                <div className="content-container">
                    <Sidebar/>
                        <div className="dashboard-container">
                            <Routes>
                                <Route path="/" element={<RecentOrders/>}/>
                                <Route path="/order-details" element={<OrderDetails/>}/>   
                                <Route path="/menu-list" element={<MenuList/>}/>
                                <Route path="/add-menu" element={<AddToMenu/>}/> 
                                <Route path="/edit-menu" element={<EditMenu/>}/> 
                                <Route path='/logout' element={<MyCart/>}/> 
                            </Routes>
                        </div>
                </div>
        </BrowserRouter>
    )
}

export default App;
